function onHandleClick(e){var t=e.closest(".content__container").querySelector(".content__slider"),n=parseInt(getComputedStyle(t).getPropertyValue("--slider-index"));e.classList.contains("content__handle--left")&&t.style.setProperty("--slider-index",n-1),e.classList.contains("content__handle--right")&&t.style.setProperty("--slider-index",n+1)}document.addEventListener("click",e=>{let t;null!=(t=e.target.matches(".content__handle")?e.target:e.target.closest(".content__handle"))&&onHandleClick(t)});